import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import AppContext from '../Context/appContext';

const Card = ({ name, username, id }) => {
  const { dispatch } = useContext(AppContext);

  const addFav = () => {  
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    const isAlreadyFavorite = favorites.some(fav => fav.id === id);
    if (!isAlreadyFavorite) {
      const newFavorite = { id, name, username };
      const updatedFavorites = [...favorites, newFavorite];

      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));

      console.log(`Añadir al favorito: ${id}`);
    } else {
      console.log(`La tarjeta con ID ${id} ya está en favoritos.`);
  }
    

    console.log(`Añadir al favorito: ${id}`);
  };

  return (
    <div className="card">
      <Link to={`/dentist/${id}`}>
        {/* En cada tarjeta mostrar el name, username y el id */}
        <h2>{name}</h2>
        <p>Username: {username}</p>
        <p>ID: {id}</p>
        {/* Adicionalmente, puedes mostrar otros detalles según tus necesidades */}
      </Link>
      <button onClick={addFav} className="favButton">
        Add fav
      </button>
    </div>
  );
};

export default Card;
