import React, { useContext, useEffect, useState } from 'react';
import AppContext from '../Context/appContext';

const Home = () => {
  const { state, dispatch } = useContext(AppContext);
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    const fetchDentists = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        dispatch({ type: 'SET_API_DATA', payload: data }); // Almacenar los datos en el estado global
        setDentists(data);
      } catch (error) {
        console.error('Error fetching dentists:', error);
      }
    };

    fetchDentists();
  }, [dispatch]);

  const addToFavorites = (dentistId) => {
    dispatch({ type: 'ADD_TO_FAVORITES', payload: dentistId });
  };

  return (
    <div>
      <h1>Listado de Dentistas</h1>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '16px' }}>
        {dentists.map((dentist) => (
          <div key={dentist.id} style={{ border: '1px solid #ccc', padding: '16px', borderRadius: '8px' }}>
            <h2>{dentist.name}</h2>
            <p>Username: {dentist.username}</p>
            <button onClick={() => addToFavorites(dentist.id)}>ADD FAV</button>
            <a href={`/dentist/${dentist.id}`}>Ver Detalles</a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
