import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AppContext from '../Context/appContext';
import Card from '../Components/Card'

const Home = () => {
  const { state, dispatch } = useContext(AppContext);
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    if (state.apiData) {
      const dentists = state.apiData || [];
      setDentists(state.apiData);
    }
  }, [state.apiData]);

  useEffect(() => {
    const fetchDentists = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        dispatch({ type: 'SET_API_DATA', payload: data }); // Almacenar los datos en el estado global
        setDentists(data);
      } catch (error) {
        console.error('Error fetching dentists:', error);
      }
    };

    fetchDentists();
  }, [dispatch]);

  const addToFavorites = (dentistId) => {
    dispatch({ type: 'ADD_TO_FAVORITES', payload: dentistId });
  };

  return (
    <div>
      <h1>Lista de Dentistas</h1>
      {apiData ? (
        <div className="card-container">
          {apiData.map((dentist) => (
            <Link key={dentist.id} to={`/dentist/${dentist.id}`}>
              <Card
                key={dentist.id}
                id={dentist.id}
                name={dentist.name}
                username={dentist.username}
              />
            </Link>
          ))}
        </div>
      ) : (
        <p>Cargando dentistas...</p>
      )}
    </div>
  );
};

export default Home;
