import React from "react";
import { Link } from 'react-router-dom';

const Card = ({ name, username, id }) => {
  const { dispatch } = useContext(AppContext);
  
  const addFav = () => {
    dispatch({ type: 'ADD_TO_FAVORITES', payload: id });
  
    console.log(`Añadir al favorito: ${id}`);
  };

  return (
    <div className="card">
      <Link to={`/dentist/${id}`}>
        {/* En cada tarjeta mostrar el name, username y el id */}
        <h2>{name}</h2>
        <p>Username: {username}</p>
        <p>ID: {id}</p>
        {/* Adicionalmente, puedes mostrar otros detalles según tus necesidades */}
      </Link>
      <button onClick={addFav} className="favButton">
        Add fav
      </button>
    </div>
  );
};

export default Card;
